mapper.select:
SELECT ORDER_NUM , MAX(MEM_ID) MEM_ID , SUM((SELECT PRICE FROM BOOK WHERE BOOK_NUM = shop_buy.BOOK_NUM) * BUY_CNT) PRICE , MAX(BUY_DATE) BUY_DATE , CASE COUNT(ORDER_NUM) - 1 WHEN 0 THEN MAX((SELECT TITLE FROM BOOK WHERE BOOK_NUM = shop_buy.BOOK_NUM)) ELSE CONCAT(MAX((SELECT TITLE FROM BOOK WHERE BOOK_NUM = shop_buy.BOOK_NUM)) , ' 외 ' , COUNT(ORDER_NUM) - 1 , '건' ) END TITLE FROM shop_buy WHERE 1 = 1
AND ORDER_NUM = #{orderNum}
AND MEM_ID LIKE CONCAT('%', #{memId}, '%')
AND BUY_DATE >= #{fromDate}
AND BUY_DATE <= ADDDATE(#{toDate}, 1)
GROUP BY ORDER_NUM ORDER BY BUY_DATE DESC;

mapper.insert:
INSERT INTO SHOP_BUY (
    BOOK_NUM
    , MEM_ID
    , BUY_CNT
    , ORDER_NUM
) VALUES (
    #{bookNum}
    , #{memId}
    , #{buyCnt}
    , (SELECT IFNULL(MAX(ORDER_NUM), 0) + 1 FROM SHOP_BUY)
)
